    },
    {
      "keystrokes": "gzip -c current/router.conf > backups/router.conf.orig.gz\n",
      "duration": 0.1
    },
    {
      "keystrokes": "dos2unix current/router.conf\n",
      "duration": 0.1
    },
    {
      "keystrokes": "echo \"$(date -u +'%Y-%m-%dT%H:%M:%SZ') - NORMALIZED_LF - OK\" >> logs/patch_application.log\n",
