    },
    {
      "keystrokes": "diff -u current/router.conf baseline/router.conf > patches/router_fix.patch\n",
      "duration": 0.1
    },
    {
      "keystrokes": "echo \"$(date -u +'%Y-%m-%dT%H:%M:%SZ') - CREATED_PATCH - OK\" >> logs/patch_application.log\n",
      "duration": 0.1
    },
    {
      "keystrokes": "ORIGINAL_SIZE=$(stat --printf=\"%s\" current/router.conf.orig)\n",
      "duration": 0.1
    },
    {
      "keystrokes": "PATCH_SIZE=$(stat --printf=\"%s\" patches/router_fix.patch)\n",
      "duration": 0.1
    },
    {
      "keystrokes": "echo \"{\\\"original_size_bytes\\\": $ORIGINAL_SIZE, \\\"patch_size_bytes\\\": $PATCH_SIZE}\" > logs/metrics.json\n",
      "duration": 0.1
    },
    {
      "keystrokes": "echo \"$(date -u +'%Y-%m-%dT%H:%M:%SZ') - METRICS_LOGGED - OK\" >> logs/patch_application.log\n",
      "duration": 0.1
    },
    {
      "keystrokes": "patch -s -p0 < patches/router_fix.patch\n",
      "duration": 0.1
    },
    {
      "keystrokes": "echo \"$(date -u +'%Y-%m-%dT%H:%M:%SZ') - APPLIED_PATCH - OK\" >> logs/patch_application.log\n",
      "duration": 0.1
    },
    {
      "keystrokes": "diff -q current/router.conf baseline/router.conf\n",
      "duration": 0.1
    },
    {
      "keystrokes": "if [ $? -eq 0 ]; then echo \"$(date -u +'%Y-%m-%dT%H:%M:%SZ') - VERIFIED_CONFIG - OK\" >> logs/patch_application.log; else echo \"$(date -u +'%Y-%m-%dT%H:%M:%SZ') - VERIFIED_CONFIG - FAILED\" >> logs/patch_application.log; fi\n",
      "duration": 0.1
    }
  ],
  "task_complete": true
}
