    {
      "keystrokes": "MINOR=$(echo \"$CURRENT_VERSION\" | grep -oP \"(?<=\\.)[0-9]+(?=\\.)\") && NEW_MINOR=$((MINOR + 1)) && echo \"MINOR: $MINOR -> $NEW_MINOR\" >> bump.log\n",
