    {
      "keystrokes": "NEW_PATCH=$((SUM_ASCII + MAGIC_OFFSET)) && echo \"NEW PATCH: $NEW_PATCH\" >> bump.log\n",
      "duration": 0.1
    },
    {
      "keystrokes": "echo \"$MAJOR.$NEW_MINOR.$NEW_PATCH\" | iconv -t ISO-8859-1 > version.txt\n",
