cat > Makefile << 'EOF'
.PHONY: all compile test package clean

# Read version from VERSION file, stripping whitespace
VERSION=$(shell cat VERSION | tr -d ' \n')

# Default target
all: compile test package

# Compile target with parallel execution
compile:
	@echo "Creating build directory..."
	@mkdir -p build
	@echo "Building part A..."
	@(echo 'int main(){' > build/part_a && echo "Building part A" && sleep 1) &
	@echo "Building part B..."
	@(echo 'return 0;}' > build/part_b && echo "Building part B" && sleep 1) &
	@wait
	@echo "Concatenating parts..."
	@cat build/part_a build/part_b > build/mytool
	@echo "Compilation complete: build/mytool created"

# Test target
test:
	@echo "Creating reports directory..."
	@mkdir -p reports
	@echo "TEST_SUITE_RESULT: PASS" > reports/test-results.log
	@echo "TEST_USER: $(shell whoami)" >> reports/test-results.log
	@echo "Testing complete: reports/test-results.log created"

# Package target
package:
	@echo "Creating dist directory..."
	@mkdir -p dist
	@tar -czf dist/mytool-$(VERSION).tar.gz -C build mytool -C reports test-results.log
	@sha256sum dist/mytool-$(VERSION).tar.gz > dist/mytool-$(VERSION).tar.gz.sha256
	@echo "Packaging complete: dist/mytool-$(VERSION).tar.gz and its checksum created"

# Clean target
clean:
	@echo "Cleaning build artifacts..."
	@rm -rf build reports dist
	@echo "Clean complete"
EOF

