      "keystrokes": "if [ -f \"$ARCHIVE_FILE\" ]; then TIMESTAMP_BAK=$(date +%s) && mv \"$ARCHIVE_FILE\" \"$ARCHIVE_FILE.bak-$TIMESTAMP_BAK\"; fi\n",
      "duration": 0.1
