      "keystrokes": "echo \"]\" >> temp.json\n",
      "duration": 0.1
    },
    {
      "keystrokes": "mv temp.json custody_manifest.json\n",
      "duration": 0.1
    },
    {
      "keystrokes": "head -1 custody_manifest.json\n",
      "duration": 0.1
    }
  ],
