      "duration": 0.1
    },
    {
      "keystrokes": "rm -rf \"$TMPDIR\"\n",
      "duration": 0.1
    },
    {
      "keystrokes": "ls -la /home/user/diag/\n",
      "duration": 0.5
    }
  ],
  "task_complete": true
}
