arguments.keystrokes:
MIN_LATENCY=${PING_RESULTS[0]}
MAX_LATENCY=${PING_RESULTS[0]}
for RTT in "${PING_RESULTS[@]}"; do if [ "$RTT" -gt "$MAX_LATENCY" ]; then MAX_LATENCY="$RTT"; fi; done
for RTT in "${PING_RESULTS[@]}"; do if [ "$RTT" -lt "$MIN_LATENCY" ] || [ "$MIN_LATENCY" == "" ]; then MIN_LATENCY="$RTT"; fi; done
LATENCY_MEDIAN="${PING_RESULTS[2]}"
JITTER=$((MAX_LATENCY - MIN_LATENCY))
