#!/usr/bin/env python3
import os
import json
from flask import Flask, request, jsonify, Response

app = Flask(__name__)

# Default port if environment variable is not set
DEFAULT_PORT = int(os.getenv('BACKUP_SERVICE_PORT', '9090'))

@app.route('/status', methods=['GET'])
def get_status():
    return jsonify({"status": "operational"})

@app.route('/last-backup', methods=['GET'])
def get_last_backup():
    return jsonify({"last_backup": "2023-01-15T10:30:00Z"})

@app.route('/list', methods=['GET'])
def get_list():
    items = ["backup1", "backup2", "backup3"]
    return jsonify(items)

@app.route('/trigger-backup', methods=['POST'])
def trigger_backup():
    if not request.is_json:
        return jsonify({"error": "Expected JSON"}), 400
    return jsonify({"triggered": True, "job_id": "job-12345"})

@app.route('/download/<int:backup_id>', methods=['GET'])
def download_backup(backup_id):
    if backup_id == 2:
        content = b"dummy backup content for id 2"
        response = Response(content, mimetype='application/octet-stream')
        response.headers['Content-Disposition'] = f'attachment; filename="backup_2.dump"'
        return response
    return jsonify({"error": "backup not found"}), 404

if __name__ == '__main__':
    app.run(host='0.0.0.0', port=DEFAULT_PORT, debug=False)
EOF
