#!/usr/bin/env python3
import os, sys

def verify_deliverables():
    print("=== Final Task Verification ===")

    # 1. Check backup directory contains only .gz files
    backup_dir = '/home/user/db_backups'
    sql_files_exist = False
    gz_files_exist = False

    if os.path.exists(backup_dir):
        print(f"\nDirectory: {backup_dir}")
        for item in os.listdir(backup_dir):
            path = os.path.join(backup_dir, item)
            if item.endswith('.sql'):
                sql_files_exist = True
                print(f"  ERROR: SQL file still exists: {item}")
            elif item.endswith('.gz'):
                gz_files_exist = True
                print(f"  OK: Gzipped file: {item}")
            else:
                print(f"  INFO: Other file: {item}")

        if sql_files_exist:
            print("  WARNING: Original SQL files were not removed")
        if not gz_files_exist:
            print("  ERROR: No .gz files found")
    else:
        print(f"  ERROR: Directory not found: {backup_dir}")

    # 2. Check manifest.json
    manifest_path = '/home/user/backup_logs/manifest.json'
    if os.path.exists(manifest_path):
        print(f"\n✓ manifest.json exists")
        try:
            with open(manifest_path, 'r') as f:
                content = f.read().strip()
            if content:
                print(f"  Content preview: {content[:100]}...")
            else:
                print("  WARNING: manifest.json is empty")
        except Exception as e:
            print(f"  ERROR: Could not read manifest.json: {e}")
    else:
        print(f"\n✗ manifest.json missing")

    # 3. Check compliance_report.log
    report_path = '/home/user/backup_logs/compliance_report.log'
    if os.path.exists(report_path):
        print(f"\n✓ compliance_report.log exists")
        try:
            with open(report_path, 'r', encoding='iso-8859-1', errors='ignore') as f:
                content = f.read().strip()
            if content:
                print(f"  Content preview: {content[:100]}...")
            else:
                print("  WARNING: compliance_report.log is empty")
        except Exception as e:
            print(f"  ERROR: Could not read compliance_report.log: {e}")
    else:
        print(f"\n✗ compliance_report.log missing")

if __name__ == '__main__':
    verify_deliverables()
EOF
