      "keystrokes": "export REQUESTS_VER=$(sed -e '1s/^\xef\xbb\xbf//' -e 's/^[ \t]*//' -e 's/[ \t]*$//' /usr/local/etc/pkg.conf | awk -F= '$1 == \"requests\" {print $2}')\n",
