    },
    {
      "keystrokes": "ss -t state established | wc -l\n",
      "duration": 0.1
    },
    {
      "keystrokes": "ping -c 5 127.0.0.1 | tail -n2 | head -n1 | awk '{print $7}' | sed 's/mdev\\///' | xargs printf \"%.3f\"\n",
      "duration": 0.5
    }
  ],
  "task_complete": false
}

